package com.example.leparisien.ui.componentsimport androidx.compose.foundation.layout.PaddingValuesimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.shape.CircleShapeimport androidx.compose.material.Buttonimport androidx.compose.material.ButtonDefaultsimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.scaleimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dp@Composablefun RoundedButton(    text: String,    modifier: Modifier = Modifier,    onButtonClick: () -> Unit) {    Button(        onClick = onButtonClick,        modifier = modifier            .padding(start = 32.dp),        shape = CircleShape,        colors = ButtonDefaults.buttonColors(backgroundColor = Color(0xff_ffcc33)),        elevation = ButtonDefaults.elevation(0.dp, 0.dp),        contentPadding = PaddingValues(0.dp)    ) {        Text(            text = text,            modifier = Modifier.scale(0.7f)        )    }}