package com.example.leparisien.ui.navigationimport androidx.annotation.DrawableResimport com.example.leparisien.R// 5. Créer une classe pour représenter les éléments de la BottomNavBar.// Cette classe contiendra l'identifiant, l'icône et le titre de chaque élément.sealed class BottomNavItem(        val route: String,        @DrawableRes val filledIcon: Int,        @DrawableRes val outlinedIcon: Int,        val title: String) {    object Screen1 : BottomNavItem("Screen1", R.drawable.filled_home, R.drawable.home, "A la Une")    object Screen2 : BottomNavItem("Screen2", R.drawable.filled_dashboard, R.drawable.dashboard, "Rubriques")    object Screen3 : BottomNavItem("Screen3", R.drawable.filled_explore, R.drawable.explore, "Découvrir")    object Screen4 : BottomNavItem("Screen4", R.drawable.filled_newspaper, R.drawable.newspaper, "Journal")    object Screen5 : BottomNavItem("Screen5", R.drawable.filled_person, R.drawable.profile, "Moi")}