package com.example.leparisien.ui.screensimport androidx.compose.runtime.Composableimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport com.example.leparisien.ui.navigation.Screen// 4. Créer le NavHost pour gérer la navigation entre les écrans.@Composablefun NavGraph(navController: NavHostController,startDestination: Screen = Screen.Screen1) {    NavHost(navController = navController, startDestination = startDestination.route) {        composable(Screen.Screen1.route) { Screen1() }        composable(Screen.Screen2.route) { Screen2() }        composable(Screen.Screen3.route) { Screen3() }        composable(Screen.Screen4.route) { Screen4() }        composable(Screen.Screen5.route) { Screen5() }    }}